mapper.select:
SELECT A.*, M.nickname AS extra__writerName FROM article AS A INNER JOIN `member` AS M ON A.memberId = M.id
AND A.boardId = #{boardId}
AND ${searchFrom} LIKE '%${searchWord}%'
AND ( A.title LIKE '%${searchWord}%' OR A.body LIKE '%${searchWord}%' )
ORDER BY A.id DESC LIMIT #{limitFrom} , #{itemsInAPage}

mapper.update:
UPDATE article
title = #{title}, `body` = #{body}, updateDate = NOW()
WHERE id = #{id}

UPDATE article
SET hit = hit+1
WHERE id = ${id}

UPDATE article
SET goodReactionPoint = goodReactionPoint+#{point}
WHERE id = ${relId}

UPDATE article
SET badReactionPoint = badReactionPoint+#{point}
WHERE id = ${relId}
